import React, {useState} from 'react';
import {Button, Form} from 'react-bootstrap';
import {addWorkout} from '../user/services/user-services';

const AddWorkout = () => {
    const [workout, setWorkout] = React.useState({
        date: "",
        sport: "",
        times: "",
        weight: "",
        person:"Lilly",
        userType:"professional"
      });
    const sendWorkoutToDB = () => {
        let workoutObject = {
            date : workout.date,
            sport : workout.sport,
            times : workout.times,
            weight : workout.weight,
            person : workout.person,
            userType : workout.userType
        };
        console.log(workout.sport);
    }
    return(
        <>
            <Form>
                <h6>Kirjoita treenin tiedot alla oleviin kenttiin</h6>
                <Form.Label>Päivämäärä:</Form.Label>
                <Form.Control type="date" id="date" onChange={e => setWorkout({ date: e.target.value })}/>
                <Form.Label>Laji:</Form.Label>
                <Form.Control type="text" id="sport" onChange={e => setWorkout({ sport: e.target.value })}/>
                <Form.Label>Suorituskerrat:</Form.Label>
                <Form.Control type="number" onChange={e => setWorkout({ times: e.target.value })}/>
                <Form.Label>Paino:</Form.Label>
                <Form.Control type="number" onChange={e => setWorkout({ weight: e.target.value })}/>
                <br />
                <Button onClick = {sendWorkoutToDB}>Lisää</Button>
            </Form>
            
        </>
    )
}
export default AddWorkout;
