import React, {useState, useEffect, useContext} from 'react';
import {Button, Form, Alert} from 'react-bootstrap';
import UserContext from '../contexts/UserContext';
import ErrorContext from '../contexts/ErrorContext';
import {getResultsByUser, deleteWorkout} from '../user/services/user-services';

const DeleteWorkoutFromDB = () => {
    const [results, setResults] = useState([]);
    const userContext = useContext(UserContext);
    const errorContext = useContext(ErrorContext);
    let option; //alustetaan muuttuja. Tämän avulla saadaan valittu vaihtoehto dropdown listoissa.

    let workouts = {
        email: userContext.Email //haetaan kirjautuneen käyttäjän sähköpostiosoite contextista, ja sen avulla haetaan käyttäjän omat treenit tietokannasta
    };

    useEffect (() =>{
        getResultsByUser(workouts, userContext.token).then(response => setResults(response)); //Haetaan kirjautuneen käyttäjän treenit tietokannasta
    }, [])

    const optionHandler = (e) => {
        e.preventDefault();
        option=e.target.value;
        option = option.substr(option.lastIndexOf(" ")+1); //Erotetaan valitun treenin id
    };

    const sendWorkoutToBeDeleted = () => {
        deleteWorkout(option, userContext.token, errorContext.setAppError); //Lähetetään valittu treeni poistettavaksi
    };

    return(
        <>
            <h6>Valitse poistettava treeni alasvetovalikosta</h6>
              <Form.Group>
                <Form.Control size="sm" as="select" onChange={(e) => optionHandler(e)}>
                {results && results.map((result, index) => {
                      return <option key={index}>{result.date + " " + result.sport + " " + result._id}</option>;
                   })}
                </Form.Control>
             </Form.Group>
             <Button onClick = {sendWorkoutToBeDeleted}>Poista</Button>
          </>
    )
}
export default DeleteWorkoutFromDB;
