import React, {useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {getAllResults, deleteWorkout} from '../user/services/user-services';

const DeleteWorkoutFromDB = () => {
    const [results, setResults] = useState([]);
    let option; //initialise the variable. This is for getting the value of the selected option

    useEffect (() =>{
        getAllResults().then(response => setResults(response)); //get all results from the database, MUOKKAA ETTÄ HENKILÖN MUKAAN!
    }, [])

    const options = results.map((result, index) => //map through the results and create the options that will be shown in the UI
    <>
    <option key={index}>{result.date + " " + result.sport + " " + result._id}</option>
    </>
    );

    const optionHandler = (e) => {
        e.preventDefault();
        option=e.target.value;
        option = option.substr(option.lastIndexOf(" ")+1); //take only the id of the workout that was selected
    };

    const sendWorkoutToBeDeleted = () => {
        deleteWorkout(option); //send the selected workout to the function that will delete it from the database
    };

    return(
        <>
            <h6>Valitse poistettava treeni alasvetovalikosta</h6>
              <Form.Group>
                <Form.Control size="sm" as="select" onChange={(e) => optionHandler(e)}>
                    {options}
                </Form.Control>
             </Form.Group>
             <Button onClick = {sendWorkoutToBeDeleted}>Poista</Button>
          </>
    )
}
export default DeleteWorkoutFromDB;
