import React, {useState, useContext, useEffect} from 'react';
import {Table, Modal, Button} from 'react-bootstrap';
import AddWorkout from '../components/AddWorkout';
import EditWorkout from '../components/EditWorkout';
import UserContext from '../contexts/UserContext';
import ErrorContext from '../contexts/ErrorContext';
import {getResultsByUser, deleteWorkout} from '../user/services/user-services';


const OmatTreenit = () => {
    const userContext = useContext(UserContext);
    const errorContext = useContext(ErrorContext);
    const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys
    const [modalShow2, setModalShow2] = useState(false); //Modaalin dialogin näkyvyys
    const [results, setResults] = useState([]);

    let workouts = {
      email: userContext.Email //haetaan kirjautuneen käyttäjän sähköpostiosoite contextista, ja sen avulla haetaan käyttäjän omat treenit tietokannasta
    };
    const person = userContext.Email; //Treenin poistamista varten
    const token = userContext.token;

    useEffect (() =>{
        getResultsByUser(workouts, userContext.token).then(response => setResults(response));
    }, [])

    const sendWorkoutToBeDeleted = (option) => { //Lähetetään treeni poistettavaksi
      let workoutObject = {
        option: option,
        person: person,
        token: token
    };
      deleteWorkout(workoutObject, errorContext.setAppError, userContext.setSuccess); //Lähetetään valittu treeni poistettavaksi
    };

    const closeModalWindow = () => { 
      errorContext.setAppError(null); //Tyhjennetään mahdolliset treenien muokkaamisessa jne. tulleet virheet errorContextista
      userContext.setSuccess(false); //Asetetaan success-tila falseksi, kun modaali ikkuna suljetaan
      setModalShow(false); //Suljetaan modaalinen ikkuna
    };

    function MyVerticallyCenteredModal(props) {
        const [visibleEdit, setVisibleEdit] = useState(false); //Treenien editointinäkymän näkyvyys
        const [visibleAdd, setVisibleAdd] = useState(false); //Treenien lisäysnäkymän näkyvyys

        const editButton = ( //Kun painetaa editButtonia, näytetään ainoastaan editointinäkymä ja muut näkymät suljetaan, sama deleteButtonissa ja addButtonissa
        <Button className="editWorkouts" onClick={() => {setVisibleEdit(!visibleEdit); setVisibleAdd(false);}}>Muokkaa treenejä</Button> //Painike treenien editoimisnäkymään
        );
        const addButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleAdd(!visibleAdd); setVisibleEdit(false); }}>Lisää treenejä</Button> //Painike treenien muokkausnäkymään
        );

        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
            backdrop="static"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {addButton}
                {visibleAdd && <AddWorkout />}
                <br />
                {editButton}
                {visibleEdit && <EditWorkout />}
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={closeModalWindow}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }



      function MyVerticallyCenteredModal2(props) {
        const [visibleEdit, setVisibleEdit] = useState(false); //Treenien editointinäkymän näkyvyys
        const [visibleAdd, setVisibleAdd] = useState(false); //Treenien lisäysnäkymän näkyvyys

        const editButton = ( //Kun painetaa editButtonia, näytetään ainoastaan editointinäkymä ja muut näkymät suljetaan, sama deleteButtonissa ja addButtonissa
        <Button className="editWorkouts" onClick={() => {setVisibleEdit(!visibleEdit); setVisibleAdd(false);}}>Muokkaa treenejä</Button> //Painike treenien editoimisnäkymään
        );
        const addButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleAdd(!visibleAdd); setVisibleEdit(false); }}>Lisää treenejä</Button> //Painike treenien muokkausnäkymään
        );

        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
            backdrop="static"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {addButton}
                {visibleAdd && <AddWorkout />}
                <br />
                {editButton}
                {visibleEdit && <EditWorkout />}
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={closeModalWindow}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }


      
    return(
        <>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={closeModalWindow}
          />
        </>
        <>
          <MyVerticallyCenteredModal2
            show={modalShow}
            onHide={closeModalWindow}
          />
        </>
        <h3 id="username">Omat treenit: {userContext.Firstname}</h3>
        <Button id="editWorkoutButton" variant="primary" onClick={() => setModalShow(true)}>Muokkaa treenejä</Button>
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Laji</th>
                    <th>Suorituskerrat</th>
                    <th>Paino</th>
                    </tr>
                </thead>
                <tbody>
                    {results && results.map((result, index) => {
                      return <tr><td key={index}>{result.date}</td><td>{result.sport}</td><td>{result.times}</td><td>{result.weight}</td><td className="removeButton"><Button onClick={() => sendWorkoutToBeDeleted(result._id)}>Poista</Button><Button onClick={() => setModalShow2(true)}>Muokkaa</Button></td></tr>;
                   })}
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default OmatTreenit;
