import React, {useCallback, useState} from 'react';
import './css.css' //voiko käyttää
import 'bootstrap/dist/css/bootstrap.min.css'; //Importataan bootstrapin tyylit

import Register from './components/Register';
import Navigation from './components/Navigation';

import UserContext from './contexts/UserContext';

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


const App = () => {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [UID, setUid] = useState('');
  const [isError, setIsError] = useState(false);
 // const [appError, setAppError] = useState(false);
  const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys
  const [validated, setValidated] = useState(false);
  const [loginState, setLoginState] = useState('true');
  const [error, setError] = useState('');
  const [showError, setShowError] = useState(false);
  const showModalDialogButton = (
    <Button variant="primary" onClick={() => setModalShow(true)}>Kirjaudu</Button>
  )
  const login = useCallback(() => {
    setLoggedIn(true);
  },[]);

  const logout = useCallback(() => {
    setLoggedIn(false);
  },[]);

  const setAppError = useCallback((err) => {
    if(err)
      setIsError(true);
    else
      setIsError(false);
    setError(err);
  },[]);

  const setUID = useCallback((uid) => {
    setUid(uid);
  },[]);

  function MyVerticallyCenteredModal(props) {
    
    const [user, setUser] = React.useState({
      uid: "",
      password: "",
      name: "",
      email: ""
    });
    const [visible, setVisible] = useState(false); //Rekisteröitymis-painiketta varten
    const button = (
      <Button id="register"onClick={() => setVisible(!visible)}>Rekisteröidy</Button> //Rekisteröitymispainikkeen näkyvyyden muuttaminen
    )
    
    const checkIsValid = () => {
      if(loginState){
        return user.uid && user.password; //Katsotaan onko molemmat kentät syötetty sisäänkirjautumisessa
      }
    }
    const uidHandler = (e) => {
      e.preventDefault();
      setUser({...user, uid:e.target.value});
    }
    const passwordHandler = (e) => {
      e.preventDefault();
      setUser({...user, password:e.target.value});
    }
    const nameHandler = (e) => { // Miten hoidetaan, kun rekisteröityminen on eri komponentissa
      e.preventDefault();
      setUser({...user, name:e.target.value});
    }
    /*const handleSubmit = (e) => {
      if(!checkIsValid()){
        alert("Tarkista syötteet!")
      } else{
        setValidated(true); //On login-tilassa ja on validi
        if(loginState){
          Userservice
          .login(user, setAppError, userContext.login, userContext.setUID)
        }
        //on rekisteröitymistilassa ja validi
        else{
          Userservice
          .signup(user, setAppError)
          switchState();
        }
      }
    }*/
    return (
      <Modal
        {...props}
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Kirjaudu sisään
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <h6>Kirjoita sähköposti ja salasana</h6>
            <Form.Label>Sähköposti:</Form.Label>
            <Form.Control type="email" placeholder="Syötä käyttäjätunnus" 
            required value={user.uid /* VAI EMAIL?*/}
            onChange={(e) => uidHandler(e)}/>
            <Form.Label>Salasana:</Form.Label>
            <Form.Control type="password" placeholder="Syötä salasana"
            required value={user.password}
            onChange={(e) => passwordHandler(e)} />
            <br />
            <Button id="logIn" onClick={() =>{setModalShow(false); setVisible(false);}}>Kirjaudu sisään</Button>
          </Form.Group>
          <br />
          <h6>Eikö sinulla ole vielä tiliä?</h6>
          {button}
          {visible && <Register />}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Sulje</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  return (
    <div>
      <UserContext.Provider value={{isLoggedIn:isLoggedIn,
      login:login, logout:logout, UID:UID, setUID:setUID}}>
      
    </UserContext.Provider>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
      <h1>
          TREENIPÄIVÄKIRJA
        </h1>
        <h6>
          Harjoitus 4, created by Jenni Lehtonen
        </h6>
      <Navigation showModalDialogButton ={showModalDialogButton}/>
    </div>
  );
}

export default App;
