import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const Register = () => {
  const [firstname, setFirstname] = React.useState('');
  const [lastname, setLastname] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  const firstnameHandler = (e) => {
    e.preventDefault();
    setFirstname(e.target.value);
  };
  const lastnameHandler = (e) => {
    e.preventDefault();
    setLastname(e.target.value);
  };
  const emailHandler = (e) => {
    e.preventDefault();
    setEmail(e.target.value);
  };
  const passwordHandler = (e) => {
    e.preventDefault();
    setPassword(e.target.value);
  };
    
  const registerNewAccount = () => {
    let accountObject = {
        firstname: firstname,
        lastname: lastname,
        email: email,
        password: password
    };
    register(accountObject);
}
    return(
        <>
          <br />
          <Form.Group>
            <Form.Label>Etunimi:</Form.Label>
            <Form.Control required type="text" placeholder="Syötä etunimesi" />
            <Form.Label>Sukunimi:</Form.Label>
            <Form.Control required type="text" placeholder="Syötä sukunimesi" />
            <Form.Label>Sähköposti:</Form.Label>
            <Form.Control required type="email" placeholder="Syötä sähköpostiosoite" />
            <Form.Label>Salasana:</Form.Label>
            <Form.Control required type="password" placeholder="Syötä salasana" />
            <Form.Check required type="checkbox" label="Olen ammattilainen" />
            <br />
            <Button>Luo tili</Button>
          </Form.Group>
          </>
    )
}
export default Register;
