import React, {useState} from 'react';
import Table from 'react-bootstrap/Table';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import DeleteWorkout from '../components/DeleteWorkout';
import AddWorkout from '../components/AddWorkout';
import EditWorkout from '../components/EditWorkout';

const OmatTreenit = () => {
    const [modalShow, setModalShow] = React.useState(false); //Modaalin dialogin näkyvyys
    const [visibleEdit, setVisibleEdit] = useState(false); //kommentti
        const [visibleAdd, setVisibleAdd] = useState(false); //kommentti
        const [visibleDelete, setVisibleDelete] = useState(false); //kommentti
    function closeTabs (props){
        if (props = "delete"){

        } else if (props = "edit"){

        } else{

        }
    }
    function MyVerticallyCenteredModal(props) {
        
        const editButton = (
        <Button className="editWorkouts" onClick={() => setVisibleEdit(!visibleEdit)}>Muokkaa treenejä</Button> //Painike treenien editoimisnäkymään
        )
        const deleteButton = (
        <Button className="editWorkouts" onClick={() => setVisibleDelete(!visibleDelete)}>Poista treenejä</Button> //Painike treenien poistonäkymään
        )
        const addButton = (
        <Button className="editWorkouts" onClick={() => setVisibleAdd(!visibleAdd)}>Lisää treenejä</Button> //Painike treenien muokkausnäkymään
        
        )
        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {deleteButton}
                {visibleDelete && <DeleteWorkout />}
                <br />
                {addButton}
                {visibleAdd && <AddWorkout />}
                <br />
                {editButton}
                {visibleEdit && <EditWorkout />}
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={props.onHide}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }

    return(
        <>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
        <Button variant="primary" onClick={() => setModalShow(true)}>Muokkaa treenejä</Button>
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Laji</th>
                    <th>Suorituskerrat</th>
                    <th>Paino</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>OMAT TREENIT</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                    <td></td>
                    </tr>
                    <tr>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td></td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <td></td>
                    <td>Larry the Bird</td>
                    <td></td>
                    <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default OmatTreenit;
