import axios from 'axios';
import {useContext} from 'react';

const baseUrl = 'http://localhost:5000/api/users'

const getAll = async () => {
    const config = {
        method: 'get',
        url: baseUrl,
        headers: {}
    }
    try {
        let res = await axios(config);
        return res.data;
    } catch (error){
        alert(error);
        return;
    };
};

/*const getProfessionalsResults = async (person) => {
    const config = {
        method: 'get',
        url: baseUrl + '/professionals/',
        headers: {}
    }
};*/
const signup = async (newObject, setError) =>{
    await axios.post(`${baseUrl}/register/`, newObject)
    .then(response => {
        return response;
    })
    .catch (error => {
        if(error.response){
            let customError = error.response;
            customError.userMessage = "Rekisteröityminen epäonnistui";
            setError(customError);
            return error;
        }
    })
}

const login = async (newObject, setError, login, setUID) => {
    await axios.post (`${baseUrl}/login/`, newObject)
    .then(response =>{
        login();
        setUID(newObject.uid);
        return response;
    })
    .catch (error => {
        if(error.response){
            let customError = error.response;
            customError.userMessage = "Käyttäjää ei ole olemassa. Tarkista käyttäjätunnuksesi ja yritä uudelleen!";
            setError(customError);
            return error;
        }
    })
}
