import React, {useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {getAllOrders, deleteWorkout} from '../user/services/user-services';

const DeleteWorkoutFromDB = () => {
    const [results, setResults] = useState([]);
    let option;

    useEffect (() =>{
        getAllOrders().then(response => setResults(response));
    }, [])
    console.log(results);

    const options = results.map((result, index) =>
    <>
    <option key={index}>{result.date + " " + result.sport + " " + result._id}</option>
    </>
    );

    const optionHandler = (e) => {
        e.preventDefault();
        option=e.target.value;
        console.log(option);
        option = option.substr(option.lastIndexOf(" ")+1); //take only the id of the workout
        console.log(option);
    };

    const sendWorkoutToBeDeleted = (option) => {
        deleteWorkout(option);
    };

    return(
        <>
            <h6>Valitse poistettava treeni alasvetovalikosta</h6>
              <Form.Group>
                <Form.Control size="sm" as="select" onChange={(e) => optionHandler(e)}>
                    {options}
                </Form.Control>
             </Form.Group>
             <Button onClick = {sendWorkoutToBeDeleted(option)}>Poista</Button>
          </>
    )
}
export default DeleteWorkoutFromDB;
