import React, {useState, useEffect, useContext} from 'react';
import {Button, Form, Alert} from 'react-bootstrap';
import {getResultsByUser, editWorkout} from '../user/services/user-services';
import UserContext from '../contexts/UserContext';
import ErrorContext from '../contexts/ErrorContext';

const EditWorkout = () => {
  const userContext = useContext(UserContext);
  const errorContext = useContext(ErrorContext);
  const [id, setId] = React.useState('');
  const [date, setDate] = React.useState('');
  const [sport, setSport] = React.useState('');
  const [times, setTimes] = React.useState('');
  const [weight, setWeight] = React.useState('');
  const [results, setResults] = useState([]);
  const [show, setShow] = useState(true); //Virheilmoituksen näyttämiseen
  const person = userContext.Email;
  const usertype = userContext.UserType;

  let option; //alustetaan muuttuja. Tämän avulla saadaan valittu vaihtoehto dropdown listoissa.
  let workouts = {
    email: userContext.Email //haetaan kirjautuneen käyttäjän sähköpostiosoite contextista, ja sen avulla haetaan käyttäjän omat treenit tietokannasta
};
  useEffect (() =>{
    getResultsByUser(workouts, userContext.token).then(response => setResults(response)); //Haetaan kirjautuneen henkilön omat treenit
  }, [])

  const idHandler = (e) => {
    e.preventDefault();
    option=e.target.value;
    option = option.substr(option.lastIndexOf(" ")+1); //Erotetaan valitun treenin id
    setId(option);
    console.log(option);
    console.log(id);
  };
  const dateHandler = (e) => {
    e.preventDefault();
    setDate(e.target.value);
  };
  const sportHandler = (e) => {
    e.preventDefault();
    setSport(e.target.value);
  };
  const timesHandler = (e) => {
    e.preventDefault();
    setTimes(e.target.value);
  };
  const weightHandler = (e) => {
    e.preventDefault();
    setWeight(e.target.value);
  };
  const sendWorkoutToBeEdited = () => {
    let workoutObject = {
      id : id,
      date : date,
      sport : sport,
      times : times,
      weight : weight,
      person : person,
      userType : usertype
  };
  editWorkout(workoutObject, userContext.token, errorContext.setAppError) //Lähetetään valittu treeni muokattavaksi
  };

  const closeErrorMessage = () => { //Kun painetaan rastia virheilmoituksessa, suljetaan virheilmoitus ja poistetaan virhe errorcontextista
    setShow(false);
    errorContext.setAppError(null);
  }
    return(
        <>
          {errorContext.appError && 
          <Alert variant="danger" onClose={() => closeErrorMessage()} dismissible>
            <Alert.Heading>Virhe</Alert.Heading>
            <p>
              {errorContext.appError}
            </p>
          </Alert>
          }
            <h6>Valitse muokattava treeni alasvetovalikosta</h6>
              <Form.Group>
                <Form.Control size="sm" as="select" onChange={(e) => idHandler(e)}>
                {results && results.map((result, index) => {
                      return <option key={index}>{result.date + " " + result.sport + " " + result._id}</option>;
                   })}
                </Form.Control>
             </Form.Group>
             <Form.Label>Päivämäärä:</Form.Label>
                <Form.Control type="date" onChange={(e) => dateHandler(e)}/>
                <Form.Label>Laji:</Form.Label>
                <Form.Control type="text" onChange={(e) => sportHandler(e)}/>
                <Form.Label>Suorituskerrat:</Form.Label>
                <Form.Control type="number" onChange={(e) => timesHandler(e)}/>
                <Form.Label>Paino:</Form.Label>
                <Form.Control type="number" onChange={(e) => weightHandler(e)}/>
                <br />
                <Button onClick = {sendWorkoutToBeEdited}>Muokkaa</Button>
        </>
    )
}
export default EditWorkout;
