import React, {useState, useContext, useEffect} from 'react';
import {Table, Modal, Button} from 'react-bootstrap';
import DeleteWorkout from '../components/DeleteWorkout';
import AddWorkout from '../components/AddWorkout';
import EditWorkout from '../components/EditWorkout';
import UserContext from '../contexts/UserContext';
import {getResultsByUser} from '../user/services/user-services';

const OmatTreenit = () => {
    const userContext = useContext(UserContext);
    const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys
    const [results, setResults] = useState([]);
    const username = userContext.Email; //haetaan kirjautuneen käyttäjän sähköpostiosoite contextista, ja sen avulla haetaan käyttäjän omat treenit tietokannasta
    
    let workouts = {
      email: userContext.Email
  };
    useEffect (() =>{
        getResultsByUser(username).then(response => setResults(response));
        console.log(results);
    }, [])

    const ownResults = results.map((result, index) => {
      return (
             <tr><td>{result.date}</td><td>{result.times}</td></tr>
      );
    });

    
    function MyVerticallyCenteredModal(props) {
        const [visibleEdit, setVisibleEdit] = useState(false); //kommentti
        const [visibleAdd, setVisibleAdd] = useState(false); //kommentti
        const [visibleDelete, setVisibleDelete] = useState(false); //kommentti
        const editButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleEdit(!visibleEdit); setVisibleAdd(false); setVisibleDelete(false)}}>Muokkaa treenejä</Button> //Painike treenien editoimisnäkymään
        )
        const deleteButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleDelete(!visibleDelete); setVisibleAdd(false); setVisibleEdit(false);}}>Poista treenejä</Button> //Painike treenien poistonäkymään
        )
        const addButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleAdd(!visibleAdd); setVisibleEdit(false); setVisibleDelete(false);}}>Lisää treenejä</Button> //Painike treenien muokkausnäkymään
        )
        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {deleteButton}
                {visibleDelete && <DeleteWorkout />}
                <br />
                {addButton}
                {visibleAdd && <AddWorkout />}
                <br />
                {editButton}
                {visibleEdit && <EditWorkout />}
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={props.onHide}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }

    return(
        <>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
        <h3 id="username">Omat treenit: {username}</h3>
        <Button id="editWorkoutButton" variant="primary" onClick={() => setModalShow(true)}>Muokkaa treenejä</Button>
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Laji</th>
                    <th>Suorituskerrat</th>
                    <th>Paino</th>
                    </tr>
                </thead>
                <tbody>
                  <tr>kgdofik</tr>
                    {ownResults}
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default OmatTreenit;
