import React, {useState, useEffect} from 'react';
import {Button, Form} from 'react-bootstrap';
import {getAllResults} from '../user/services/user-services';

const EditWorkout = () => {
  const [date, setDate] = React.useState('');
  const [sport, setSport] = React.useState('');
  const [times, setTimes] = React.useState('');
  const [weight, setWeight] = React.useState('');
  const [results, setResults] = useState([]);
  let option; //initialise the variable. This is for getting the value of the selected option

  useEffect (() =>{
      getAllResults().then(response => setResults(response)); //get all results from the database, MUOKKAA ETTÄ HENKILÖN MUKAAN!
  }, [])

  const options = results.map((result, index) => //map through the results and create the options that will be shown in the UI
  <>
  <option key={index}>{result.date + " " + result.sport + " " + result._id}</option>
  </>
  );

  const dateHandler = (e) => {
    e.preventDefault();
    setDate(e.target.value);
  };
  const sportHandler = (e) => {
    e.preventDefault();
    setSport(e.target.value);
  };
  const timesHandler = (e) => {
    e.preventDefault();
    setTimes(e.target.value);
  };
  const weightHandler = (e) => {
    e.preventDefault();
    setWeight(e.target.value);
  };
  const sendWorkoutToBeEdited = () => {
    let workoutObject = {
      date : date,
      sport : sport,
      times : times,
      weight : weight,
      person : "Lilly", //Muuta pois kovakoodatusta
      userType : "professional" //Muuta pois kovakoodatusta
  };
  //addWorkout(workoutObject);
  console.log(workoutObject);
  };
    return(
        <>
            <h6>Valitse muokattava treeni alasvetovalikosta</h6>
              <Form.Group>
                <Form.Control size="sm" as="select">
                    {options}
                </Form.Control>
             </Form.Group>
             <Form.Label>Päivämäärä:</Form.Label>
                <Form.Control type="date" onChange={(e) => dateHandler(e)}/>
                <Form.Label>Laji:</Form.Label>
                <Form.Control type="text" onChange={(e) => sportHandler(e)}/>
                <Form.Label>Suorituskerrat:</Form.Label>
                <Form.Control type="number" onChange={(e) => timesHandler(e)}/>
                <Form.Label>Paino:</Form.Label>
                <Form.Control type="number" onChange={(e) => weightHandler(e)}/>
                <br />
                <Button onClick = {sendWorkoutToBeEdited}>Muokkaa</Button>
        </>
    )
}
export default EditWorkout;
