import axios from 'axios';
import {useContext} from 'react';

const baseUrlUsers = 'http://localhost:5000/api/users';
const baseUrlResults = 'http://localhost:5000/api/results';

const addWorkout = async () => {
    const config = {
        method: 'post',
        url: baseUrl,
        headers: {}
    }
    try {
        let res = await axios(config);
        return res.data;
    } catch (error){
        alert(error);
        return;
    };
};

const getProfessionalsResults = async () => {
    
    const config = {
        method: 'get',
        url: baseUrlResults + '/professionals/',
        headers: {}
    }
    try {
        let res = await axios(config);
        return res.data;
    } catch (error){
        alert(error);
        return;
    };
};

const getAmateursResults = async () => {
    
    const config = {
        method: 'get',
        url: baseUrlResults + '/amateurs/',
        headers: {}
    }
    try {
        let res = await axios(config);
        return res.data;
    } catch (error){
        alert(error);
        return;
    };
};

const signup = async (newObject, setError) =>{
    await axios.post(`${baseUrlUsers}/register/`, newObject)
    .then(response => {
        return response;
    })
    .catch (error => {
        if(error.response){
            let customError = error.response;
            customError.userMessage = "Rekisteröityminen epäonnistui";
            setError(customError);
            return error;
        }
    })
}

const login = async (newObject, setError, login, setUID) => {
    await axios.post (`${baseUrlUsers}/login/`, newObject)
    .then(response =>{
        login();
        setUID(newObject.uid);
        return response;
    })
    .catch (error => {
        if(error.response){
            let customError = error.response;
            customError.userMessage = "Käyttäjää ei ole olemassa. Tarkista käyttäjätunnuksesi ja yritä uudelleen!";
            setError(customError);
            return error;
        }
    })
}
//export default getAmateursResults;
export {getAmateursResults, getProfessionalsResults}
