import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Register from '../components/Register';

const ModalDialog = (props) => {
    const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys
    const [visible, setVisible] = useState(false); //Rekisteröitymis-painiketta varten
    const button = (
      <Button id="register"onClick={() => setVisible(!visible)}>Rekisteröidy</Button> //Rekisteröitymispainikkeen näkyvyyden muuttaminen
    )
    return(
        <>
           <Modal
        {...props}
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Kirjaudu sisään
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <h6>Kirjoita sähköposti ja salasana</h6>
            <Form.Label>Sähköpost:</Form.Label>
            <Form.Control type="email" placeholder="Syötä käyttäjätunnus" />
            <Form.Label>Salasana:</Form.Label>
            <Form.Control type="password" placeholder="Syötä salasana" />
            <br />
            <Button type="submit" id="logIn" onClick={() =>{setModalShow(false); setVisible(false)}}>Kirjaudu sisään</Button>
          </Form.Group>
          <br />
          <h6>Eikö sinulla ole vielä tiliä?</h6>
          {button}
          {visible && <Register />}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Sulje</Button>
        </Modal.Footer>
      </Modal>
          </>
    )
}
export default ModalDialog;
