import React, {useCallback, useState} from 'react';
import './css.css';
import 'bootstrap/dist/css/bootstrap.min.css'; //Importataan bootstrapin tyylit

import Navigation from './components/Navigation';
import UserContext from './contexts/UserContext';



const App = () => {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [Email, setEmail2] = useState('');
  const [isError, setIsError] = useState(false);
  const [appError, setError] = useState('');
  const [showError, setShowError] = useState(false);

  const login = useCallback(() => {
    setLoggedIn(true);
  },[]);

  const logout = useCallback(() => {
    setLoggedIn(false);
  },[]);

  const setAppError = useCallback((err) => {
    if(err)
      setIsError(true);
    else
      setIsError(false);
    setError(err);
  },[]);

  const setEmail = useCallback((email) => {
    setEmail2(email);
  },[]);

  return (
    <div>
      <UserContext.Provider value={{isLoggedIn:isLoggedIn,
      login:login, logout:logout, Email:Email, setEmail:setEmail}}>
       <Navigation />
       
    </UserContext.Provider>
    </div>
  );
}

export default App;
