import React, {useState} from 'react';
import './css.css' //voiko käyttää

import Etusivu from './components/Etusivu';
import Harrastajat from './components/Harrastajat';
import Ammattilaiset from './components/Ammattilaiset';
import OmatTreenit from './components/OmatTreenit';
import Register from './components/Register';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // font awesome ikonit käyttöön
import { faHome } from '@fortawesome/free-solid-svg-icons';
import {BrowserRouter as Router, Route, Redirect} from 'react-router-dom'; //Applikaation sivujen reititys
import 'bootstrap/dist/css/bootstrap.min.css'; //Importataan bootstrapin tyylit
import Nav from 'react-bootstrap/Nav';
import {LinkContainer} from 'react-router-bootstrap';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';



const App = () => {
  const element = <FontAwesomeIcon icon={faHome} size="2x" color="black" /> //Koti-ikoni navigoinnissa
  const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys

  const [loginState, setLoginState] = useState('true');
  const [error, setError] = useState('');
  const [showError, setShowError] = useState(false);
  
  function MyVerticallyCenteredModal(props) {
    const [validated, setValidated] = useState(false);
    const [user, setUser] = React.useState({
      uid: "",
      password: "",
      name: "",
      email: ""
    });
    const [visible, setVisible] = useState(false); //Rekisteröitymis-painiketta varten
    const button = (
      <Button id="register"onClick={() => setVisible(!visible)}>Rekisteröidy</Button> //Rekisteröitymispainikkeen näkyvyyden muuttaminen
    )
    const checkIsValid = () => {
      if(loginState){
        return user.uid && user.password;
      }
    }
    const uidHandler = (e) => {
      e.preventDefault();
      setUser({...user, uid:e.target.value});
    }
    const passwordHandler = (e) => {
      e.preventDefault();
      setUser({...user, password:e.target.value});
    }
    const nameHandler = (e) => { // Miten hoidetaan, kun rekisteröityminen on eri komponentissa
      e.preventDefault();
      setUser({...user, name:e.target.value});
    }
    const handleSubmit = (e) => {
      if(!checkIsValid()){
        alert("Tarkista syötteet!")
      } else{
        setValidated(true);
      }
    }
    return (
      <Modal
        {...props}
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Kirjaudu sisään
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <h6>Kirjoita sähköposti ja salasana</h6>
            <Form.Label>Sähköposti:</Form.Label>
            <Form.Control type="email" placeholder="Syötä käyttäjätunnus" 
            required value={user.uid /* VAI EMAIL?*/}
            onChange={(e) => uidHandler(e)}/>
            <Form.Label>Salasana:</Form.Label>
            <Form.Control type="password" placeholder="Syötä salasana"
            required value={user.password}
            onChange={(e) => passwordHandler(e)} />
            <br />
            <Button id="logIn" onClick={() =>{setModalShow(false); setVisible(false); handleSubmit();}}>Kirjaudu sisään</Button>
          </Form.Group>
          <br />
          <h6>Eikö sinulla ole vielä tiliä?</h6>
          {button}
          {visible && <Register />}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Sulje</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  return (
    <div>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
      <h1>
          TREENIPÄIVÄKIRJA
        </h1>
        <h6>
          Harjoitus 4, created by Jenni Lehtonen
        </h6>
    
        <>
            

        <Router>
          <Nav className="justify-content-center">
            <LinkContainer to={'/'} exact>
            <Nav.Item>
              {element} home
            </Nav.Item>
            </LinkContainer>
            <LinkContainer to={'/harrastajat'}>
            <Nav.Item>
              Harrastajat
            </Nav.Item>
            </LinkContainer>
            <LinkContainer to={'/ammattilaiset'}>
            <Nav.Item>
              Ammattilaiset
            </Nav.Item>
            </LinkContainer>
            <LinkContainer to={'Omattreenit'}>
            <Nav.Item>
              Omat treenit
            </Nav.Item>
            </LinkContainer>
            <Nav.Item>
                <Button variant="primary" onClick={() => setModalShow(true)}>Kirjaudu</Button>
            </Nav.Item>
          </Nav>
          
          <Route exact path="/" ><Etusivu /></Route>
            <Route exact path="/harrastajat" ><Harrastajat /></Route>
            <Route exact path="/ammattilaiset" ><Ammattilaiset /></Route>
            <Route exact path="/omattreenit" ><OmatTreenit /></Route>
            <Redirect to="/" />
        </Router>
          
        </>
      

    </div>
  );
}

export default App;
