import React, {useState, useEffect} from 'react';
import Table from 'react-bootstrap/Table';
import {getAmateursResults} from '../user/services/user-services';

const Harrastajat = () => {
    const [results, setResults] = useState([]);
    useEffect (() =>{
        getAmateursResults().then(response => setResults(response));
    }, [])

    let personCount = 0; //suorittajalkm:n laskemiseksi
    let workoutCount = 0; //suoritusten laskemiseksi
    const personCounter = () => {
        return personCount++;
    };
    
    for (let i=0; i < results.length; i++){
        workoutCount += results.[i].times + workoutCount;
    }
   
    let personArray = results.map(personCounter);
    console.log(personArray);
    console.log(workoutCount);
    
    const professionalsResults = results.map((result, index) =>
    <>
    <tr><td>{result.date}</td><td>{workoutCount}</td>
    <td>{personCount}</td></tr>
    </>
    );
    
    return(
        <>
        <h2 style={{textAlign: "center"}}>Harrastajat</h2><br />
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Suoritukset yhteensä</th>
                    <th>Suorittajalkm</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>Eka päivä</td>
                        <td>{workoutCount}</td>
                        <td>{personCount}</td>
                    </tr>
                    {professionalsResults}
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default Harrastajat;
