import React, {useState} from 'react';
import Table from 'react-bootstrap/Table';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';

const OmatTreenit = () => {
    const [modalShow, setModalShow] = React.useState(false); //Modaalin dialogin näkyvyys

    function MyVerticallyCenteredModal(props) {
        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>

              <h6>Valitse muokattava treeni alasvetovalikosta</h6>
              <Dropdown>
    <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
      Custom toggle
    </Dropdown.Toggle>

    <Dropdown.Menu as={CustomMenu}>
      <Dropdown.Item eventKey="1">Red</Dropdown.Item>
      <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
      <Dropdown.Item eventKey="3" active>
        Orange
      </Dropdown.Item>
      <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>
    </Dropdown.Menu>
  </Dropdown>,
);
              <label>
                Käyttäjätunnus:
                <br />
                <input></input>
              </label>
              <br />
              <label>
                Salasana:
                <br />
                <input></input>
              </label>
              <br />
              <Button>Muokkaa</Button>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={props.onHide}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }

      // The forwardRef is important!!
// Dropdown needs access to the DOM node in order to position the Menu
const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <a
      href=""
      ref={ref}
      onClick={(e) => {
        e.preventDefault();
        onClick(e);
      }}
    >
      {children}
      &#x25bc;
    </a>
  ));
  
  // forwardRef again here!
  // Dropdown needs access to the DOM of the Menu to measure it
  const CustomMenu = React.forwardRef(
    ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
      const [value, setValue] = useState('');
  
      return (
        <div
          ref={ref}
          style={style}
          className={className}
          aria-labelledby={labeledBy}
        >
          <FormControl
            autoFocus
            className="mx-3 my-2 w-auto"
            placeholder="Type to filter..."
            onChange={(e) => setValue(e.target.value)}
            value={value}
          />
          <ul className="list-unstyled">
            {React.Children.toArray(children).filter(
              (child) =>
                !value || child.props.children.toLowerCase().startsWith(value),
            )}
          </ul>
        </div>
      );
    },
  );
    return(
        <>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
        <Button variant="primary" onClick={() => setModalShow(true)}>Muokkaa treenejä</Button>
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Laji</th>
                    <th>Suorituskerrat</th>
                    <th>Paino</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>OMAT TREENIT</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                    <td></td>
                    </tr>
                    <tr>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td></td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <td></td>
                    <td>Larry the Bird</td>
                    <td></td>
                    <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default OmatTreenit;
