import React, {useContext, useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import {Button, Form} from 'react-bootstrap';
import UserContext from '../contexts/UserContext';
import UserService from './services/user-services';

import Register from '../components/Register';

const Login = () => {
  const userContext = useContext(UserContext);
  const [loginState, setLoginState] = useState('true');
  const [validated, setValidated] = useState(false);
  const [error, setError] = useState('');
  const [appError, setAppError] = useState(false);
  const [modalShow, setModalShow] = React.useState(false);
    function MyVerticallyCenteredModal(props) {
    
      const [user, setUser] = React.useState({
        uid: "",
        password: "",
        name: "",
        email: ""
      });
      const [visible, setVisible] = useState(false); //Rekisteröitymis-painiketta varten
      const button = (
        <Button id="register"onClick={() => setVisible(!visible)}>Rekisteröidy</Button> //Rekisteröitymispainikkeen näkyvyyden muuttaminen
      );
      
      const checkIsValid = () => {
        if(loginState){
          return user.uid && user.password; //Katsotaan onko molemmat kentät syötetty sisäänkirjautumisessa
        }
      };
      const uidHandler = (e) => {
        e.preventDefault();
        setUser({...user, uid:e.target.value});
      };
      const emailHandler = (e) => {
        e.preventDefault();
        setUser({...user, email:e.target.value});
      };
      const passwordHandler = (e) => {
        e.preventDefault();
        setUser({...user, password:e.target.value});
      };
      const nameHandler = (e) => { // Miten hoidetaan, kun rekisteröityminen on eri komponentissa
        e.preventDefault();
        setUser({...user, name:e.target.value});
      };
      const switchState = () => {
        setLoginState(!loginState);
      };
      const handleSubmit = (e) => {
        if(!checkIsValid()){
          alert("Tarkista syötteet!")
        } else{
          setValidated(true); //On login-tilassa ja on validi
         /* if(loginState){
            Userservice
            .login(user, setAppError, UserContext.login, UserContext.setUID)
          }
          //on rekisteröitymistilassa ja validi
          else{
            Userservice
            .signup(user, setAppError)
            switchState();
          }*/
        }
      }
        return(
          <Modal
        {...props}
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Kirjaudu sisään
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <h6>Kirjoita sähköposti ja salasana</h6>
            <Form.Label>Sähköposti:</Form.Label>
            <Form.Control type="email" placeholder="Syötä käyttäjätunnus" 
            required value={user.uid /* VAI EMAIL?*/}
            onChange={(e) => uidHandler(e)}/>
            <Form.Label>Salasana:</Form.Label>
            <Form.Control type="password" placeholder="Syötä salasana"
            required value={user.password}
            onChange={(e) => passwordHandler(e)} />
            <br />
            <Button id="logIn" onClick={() =>{setModalShow(false); setVisible(false); handleSubmit();}}>Kirjaudu sisään</Button>
          </Form.Group>
          <br />
          <h6>Eikö sinulla ole vielä tiliä?</h6>
          {button}
          {visible && <Register />}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Sulje</Button>
        </Modal.Footer>
      </Modal>
        );
    }
    return(
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
          <Button variant="primary" onClick={() => setModalShow(true)}>Kirjaudu</Button>
        </>
    )
}
export default Login;
