import React, {useState, useContext, useEffect} from 'react';
import {Table, Modal, Button} from 'react-bootstrap';
import DeleteWorkout from '../components/DeleteWorkout';
import AddWorkout from '../components/AddWorkout';
import EditWorkout from '../components/EditWorkout';
import UserContext from '../contexts/UserContext';
import ErrorContext from '../contexts/ErrorContext';
import {getResultsByUser, deleteWorkout} from '../user/services/user-services';


const OmatTreenit = () => {
    const userContext = useContext(UserContext);
    const errorContext = useContext(ErrorContext);
    const [modalShow, setModalShow] = useState(false); //Modaalin dialogin näkyvyys
    const [results, setResults] = useState([]);
    const sendWorkoutToBeDeleted = (option) => {
      console.log(option);
      console.lof("painettu");
      deleteWorkout(option, userContext.token, errorContext.setAppError, userContext.setSuccess); //Lähetetään valittu treeni poistettavaksi
    };
    let workouts = {
      email: userContext.Email //haetaan kirjautuneen käyttäjän sähköpostiosoite contextista, ja sen avulla haetaan käyttäjän omat treenit tietokannasta
  };
    useEffect (() =>{
        getResultsByUser(workouts, userContext.token).then(response => setResults(response));
    }, [])

    const closeModalWindow = () => { 
      errorContext.setAppError(null); //Tyhjennetään mahdolliset treenien muokkaamisessa jne. tulleet virheet errorContextista
      userContext.setSuccess(false); //Asetetaan success-tila falseksi, kun modaali ikkuna suljetaan
      setModalShow(false); //Suljetaan modaalinen ikkuna
    };

    const hideUserMessages = () => {
      errorContext.setAppError(null);
      userContext.setSuccess(false);
    };

    function MyVerticallyCenteredModal(props) {
        const [visibleEdit, setVisibleEdit] = useState(false); //Treenien editointinäkymän näkyvyys
        const [visibleAdd, setVisibleAdd] = useState(false); //Treenien lisäysnäkymän näkyvyys
        const [visibleDelete, setVisibleDelete] = useState(false); //Treenien poistamisnäkymän näkyvyys

        const editButton = ( //Kun painetaa editButtonia, näytetään ainoastaan editointinäkymä ja muut näkymät suljetaan, sama deleteButtonissa ja addButtonissa
        <Button className="editWorkouts" onClick={() => {setVisibleEdit(!visibleEdit); setVisibleAdd(false); setVisibleDelete(false); }}>Muokkaa treenejä</Button> //Painike treenien editoimisnäkymään
        )
        const deleteButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleDelete(!visibleDelete); setVisibleAdd(false); setVisibleEdit(false); }}>Poista treenejä</Button> //Painike treenien poistonäkymään
        )
        const addButton = (
        <Button className="editWorkouts" onClick={() => {setVisibleAdd(!visibleAdd); setVisibleEdit(false); setVisibleDelete(false); }}>Lisää treenejä</Button> //Painike treenien muokkausnäkymään
        )

        return (
          <Modal
            {...props}
            aria-labelledby="contained-modal-title-vcenter"
            centered
            backdrop="static"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Muokkaa omia treenejä
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {deleteButton}
                {visibleDelete && <DeleteWorkout />}
                <br />
                {addButton}
                {visibleAdd && <AddWorkout />}
                <br />
                {editButton}
                {visibleEdit && <EditWorkout />}
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={closeModalWindow}>Sulje</Button>
            </Modal.Footer>
          </Modal>
        );
      }


      
    return(
        <>
        <>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={closeModalWindow}
          />
        </>
        <h3 id="username">Omat treenit: {userContext.Firstname}</h3>
        <Button id="editWorkoutButton" variant="primary" onClick={() => setModalShow(true)}>Muokkaa treenejä</Button>
        <div id="table-div">
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Pvm</th>
                    <th>Laji</th>
                    <th>Suorituskerrat</th>
                    <th>Paino</th>
                    </tr>
                </thead>
                <tbody>
                    {results && results.map((result, index) => {
                      return <tr><td>{result.date}</td><td>{result.sport}</td><td>{result.times}</td><td>{result.weight}</td><td><Button onClick={sendWorkoutToBeDeleted(result._id)}>Poista</Button></td></tr>;
                   })}
                </tbody>
            </Table>
        </div>
        </>
    )
}
export default OmatTreenit;
